import React, { Component } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { loadUser } from "../../store/actions/authActions";
import moment from "moment";
import {
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Button,
  Container,
  Input,
} from "reactstrap";
import { Link, Redirect } from "react-router-dom";
import HorizontalScroller from "react-horizontal-scroll-container";
export class MisTarjetasTest extends Component {
  state = {
    selectedOption: null,
    numeroVerde: "",
    numeroRojo: "",
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  render() {
    var filterVerde = {
      numero: this.state.numeroVerde && this.state.numeroVerde,
    };
    var filterRojo = {
      numero: this.state.numeroRojo && this.state.numeroRojo,
    };

    const multiFilterVerde = (arr, filters) => {
      const filterKeys = Object.keys(filters);
