import React, { Component } from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container,
  Row,
  Col,
  Alert,
} from "reactstrap";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { getCampos } from "../../store/actions/camposActions";
import { connect } from "react-redux";
import RIModal from "./RIModal";
import { Redirect } from "react-router-dom";
import {
  agregarTarjeta,
  agregarTarjetaAmarilla,
} from "../../store/actions/tarjetaActions";

class AñadirKaizen extends Component {
  componentDidMount() {
    this.props.getTarjetas();
    this.props.getCampos();
  }
  state = {
    numero: "1",
    preNumero: "",
    linea: "",
    maquina: "",
    codigo: "",
    color: "",
    tema: "",
    perdidaAtacada: "",
    pilar: "",
    lider: "",
  };

  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      // Check login error
      console.log(error.msg.msg);
      if (error.id === "AGREGAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  // onChangeColor = (e) => {
  //   const numeroTarjeta = this.props.tarjetas.tarjetas.filter(
  //     ({ color }) => color === e.target.value
  //   );
  //   const numbersArr = numeroTarjeta.map(({ numero }) => numero);
  //   const highestNumber = Math.max(...numbersArr);

  //   this.setState({
  //     [e.target.name]: e.target.value,
  //     numero: parseInt(highestNumber) + 1,
  //   });
  // };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      numero,
      linea,
      maquina,
      codigo,
      color,
      preNumero,
      tema,
      perdidaAtacada,
      pilar,
      lider,
    } = this.state;

    // Crear Tarjeta Kaizen
    const nuevoKaizen = {
      numero,
      linea,
      maquina,
      codigo,
      color,
      preNumero,
      tema,
      perdidaAtacada,
      pilar,
      lider,
    };

    console.log(nuevoKaizen);
    // this.props.agregarTarjeta(nuevoKaizen);
  };
  render() {
    if (this.props.tarjetas.agregarsuccess) {
      return <Redirect to={`/tarjetas/`} />;
    }

    const { campos } = this.props.campos;
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <h2 className="my-2">Añadir nuevo Quick Kaizen</h2>

              <Form
                className="mt-4"
                onSubmit={this.onSubmit}
                id="agregartarjeta"
              >
                <FormGroup>
                  <div>
                    <Row>
                      <Col lg={10}>
                        <Label for="color">Color *</Label>
                        <Input
                          type="select"
                          name="color"
                          id="color"
                          onChange={this.onChangeColor}
                        >
                          <option>Seleccionar</option>
                          <option>Roja</option>
                          <option>Azul</option>
                          <option>Verde</option>
                          <option>Amarilla</option>
                        </Input>
                      </Col>
                      <Col lg={2}>
                        <Label for="color">Numero *</Label>
                        <Input
                          type="number"
                          id="preNumero"
                          name="preNumero"
                          onChange={this.onChange}
                        />
                      </Col>
                    </Row>
                  </div>
                </FormGroup>

                <Row>
                  <Col>
                    <FormGroup>
                      <Label for="maquina">Maquina *</Label>
                      <Input
                        name="maquina"
                        id="maquina"
                        onChange={this.onChange}
                      ></Input>
                    </FormGroup>

                    <FormGroup>
                      <Label for="codigo">Código del Kaizen *</Label>
                      <Input
                        name="codigo"
                        id="codigo"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                    <FormGroup>
                      <Label for="lider">Líder del equipo de mejora*</Label>
                      <Input
                        name="lider"
                        id="lider"
                        onChange={this.onChange}
                      ></Input>
                    </FormGroup>
                    <FormGroup>
                      <Label for="tema">Tema *</Label>
                      <Input
                        type="textarea"
                        name="tema"
                        id="tema"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                  </Col>

                  <Col>
                    <FormGroup>
                      <Label for="linea">Linea *</Label>

                      <Input name="linea" id="linea" onChange={this.onChange} />
                    </FormGroup>

                    <FormGroup>
                      <Label for="perdida">Pérdida atacada *</Label>
                      <Input
                        name="perdida"
                        id="perdida"
                        onChange={this.onChange}
                      />
                    </FormGroup>

                    <FormGroup>
                      <Label for="pilar">Pilar *</Label>
                      <Input
                        type="select"
                        name="pilar"
                        id="pilar"
                        onChange={this.onChange}
                      >
                        <option>Seleccionar</option>
                        <option>Seguridad</option>
                        <option>Calidad</option>
                        <option>Mantenimiento autónomo</option>
                        <option>Mantenimiento planificado</option>
                        <option>Mejoras enfocadas</option>
                        <option>Capacitación y Entrenamiento</option>
                        <option>Ambiente</option>
                        <option>Logística</option>
                      </Input>
                    </FormGroup>
                  </Col>
                </Row>

                {this.state.msg ? (
                  <Alert color="danger" className="mt-3">
                    {this.state.msg}
                  </Alert>
                ) : null}

                <Row className="mt-3">
                  <Col>
                    <Button>Subir</Button>
                  </Col>
                </Row>
              </Form>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    campos: state.campos,
    error: state.error,
  };
};
export default connect(mapStateToProps, {
  agregarTarjeta,
  agregarTarjetaAmarilla,
  getTarjetas,
  getCampos,
})(AñadirKaizen);
