import { GET_CAMPOS, CARGANDO_CAMPOS, AGREGAR_CAMPOS } from "../actions/types";
const initState = {
  campos: [],
  cargando: false,
  agregarsuccesscampos: false,
};

export default function (state = initState, action) {
  switch (action.type) {
    case GET_CAMPOS:
      return {
        ...state,
        campos: action.payload,
        cargando: false,
        agregarsuccesscampos: false,
      };
    case AGREGAR_CAMPOS:
      return {
        ...state,
        campos: [action.payload, ...state.campos],
        agregarsuccesscampos: true,
      };

    case CARGANDO_CAMPOS:
      return {
        ...state,
        cargando: true,
      };
    default:
      return state;
  }
}
