import React, { Component } from "react";
import { connect } from "react-redux";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { Card, CardTitle, CardBody, Container, Row, Col } from "reactstrap";
import CanvasJSReact from "./canvasjs.react";

var CanvasJSChart = CanvasJSReact.CanvasJSChart;
var CanvasJS = CanvasJSReact.CanvasJS;

class DashBoard extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }

  constructor() {
    super();
    this.toggleDataSeries = this.toggleDataSeries.bind(this);
  }
  toggleDataSeries(e) {
    if (typeof e.dataSeries.visible === "undefined" || e.dataSeries.visible) {
      e.dataSeries.visible = false;
    } else {
      e.dataSeries.visible = true;
    }
    this.chart.render();
  }
  render() {
    const { tarjetas } = this.props.tarjetas;
    const abiertasEnero = tarjetas.filter(
      ({ estado, fecha }) =>
        estado === "Abierto" && fecha.substr(4, 4) === "-06-"
    );
    const fecha = tarjetas
      .filter(({ estado }) => estado === "Abierto")
      .map(({ fecha }) => fecha.substr(3.8, 4));
    console.log(fecha);
    const options = {
      animationEnabled: true,
      colorSet: "colorSet2",

      axisX: {
        valueFormatString: "MMMM",
      },

      toolTip: {
        shared: true,
      },
      legend: {
        cursor: "pointer",
        itemclick: this.toggleDataSeries,
        verticalAlign: "top",
      },
      data: [
        {
          type: "column",
          name: "Amarillas acumuladas (abiertas)",
          showInLegend: false,
          xValueFormatString: "MMMM YYYY",
          dataPoints: [
            { x: new Date(2017, 0), y: 27500 },
            { x: new Date(2017, 1), y: 29000 },
            { x: new Date(2017, 2), y: 22000 },
            { x: new Date(2017, 3), y: 26500 },
            { x: new Date(2017, 4), y: 33000 },
            { x: new Date(2017, 5), y: 37000 },
            { x: new Date(2017, 6), y: 32000 },
            { x: new Date(2017, 7), y: 27500 },
            { x: new Date(2017, 8), y: 29500 },
            { x: new Date(2017, 9), y: 43000 },
            { x: new Date(2017, 10), y: 55000 },
            { x: new Date(2017, 11), y: 39500 },
          ],
        },
        {
          type: "line",
          name: "Amarillas acumuladas (cerradas)",
          showInLegend: true,
          yValueFormatString: "$#,##0",
          dataPoints: [
            { x: new Date(2017, 0), y: 38000 },
            { x: new Date(2017, 1), y: 39000 },
            { x: new Date(2017, 2), y: 35000 },
            { x: new Date(2017, 3), y: 37000 },
            { x: new Date(2017, 4), y: 42000 },
            { x: new Date(2017, 5), y: 48000 },
            { x: new Date(2017, 6), y: 41000 },
            { x: new Date(2017, 7), y: 38000 },
            { x: new Date(2017, 8), y: 42000 },
            { x: new Date(2017, 9), y: 45000 },
            { x: new Date(2017, 10), y: 48000 },
            { x: new Date(2017, 11), y: 47000 },
          ],
        },
        {
          type: "area",
          name: "Porcentaje amarillas cerradas",
          markerBorderColor: "white",
          markerBorderThickness: 2,
          showInLegend: true,
          yValueFormatString: "$#,##0",
          dataPoints: [
            { x: new Date(2017, 0), y: 11500 },
            { x: new Date(2017, 1), y: 10500 },
            { x: new Date(2017, 2), y: 9000 },
            { x: new Date(2017, 3), y: 13500 },
            { x: new Date(2017, 4), y: 13890 },
            { x: new Date(2017, 5), y: 18500 },
            { x: new Date(2017, 6), y: 16000 },
            { x: new Date(2017, 7), y: 14500 },
            { x: new Date(2017, 8), y: 15880 },
            { x: new Date(2017, 9), y: 24000 },
            { x: new Date(2017, 10), y: 31000 },
            { x: new Date(2017, 11), y: 19000 },
          ],
        },
      ],
    };
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <div>
                <h2 className="mb-3">Evolucion de Tarjetas amarillas</h2>
              </div>

              <Row>
                <Col>
                  <Card>
                    <CardBody>
                      <CanvasJSChart
                        options={options}
                        onRef={(ref) => (this.chart = ref)}
                      />
                    </CardBody>
                  </Card>
                </Col>
                <Col>
                  <Card></Card>
                </Col>
              </Row>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
  };
};
export default connect(mapStateToProps, { getTarjetas })(DashBoard);
