import React, { Component } from "react";
import {
  Card,
  CardBody,
  CardTitle,
  CardSubtitle,
  Input,
  Table,
  Col,
  Row,
  Button,
  Form,
  FormGroup,
} from "reactstrap";

export class ComentariosDetalle extends Component {
  state = {
    mensaje: "",
    autor: "",
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const { mensaje, autor } = this.state;

    const nuevoComentario = {
      _id: "",
      comentario: {
        autor,
        mensaje,
        fecha: Date.now(),
      },
    };
    if (mensaje === "" || autor === "") {
      console.log("Error");
    } else {
      // this.props.addComment(nuevoComentario);
      console.log(nuevoComentario);
      document.getElementById("form").reset();
    }
  };
  render() {
    const { tarjetas, link_id } = this.props;
    link = window.location.ref;
    console.log(link);
    return (
      <div>
        <Card>
          <CardBody>
            <div className="d-flex align-items-center">
              <div className="">
                <h3>Comentarios</h3>
              </div>
            </div>

            <Table className="no-wrap v-middle" responsive>
              <thead>
                <tr className="border-0">
                  <th className="border-0">Autor</th>
                  <th className="border-0">Descripción</th>
                  <th className="border-0">Fecha</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>
                    <div className="d-flex no-block align-items-center">
                      <div className="">
                        <h5 className="mb-0 font-16 font-medium">
                          Federico Poi
                        </h5>
                      </div>
                    </div>
                  </td>

                  <td>Mensaje</td>
                  <td>12/12/12</td>
                </tr>
              </tbody>
            </Table>
            <hr className="mt-3"></hr>
            <Form onSubmit={this.onSubmit} id="form">
              <Row className="mb-1">
                <Col lg={8}>
                  <FormGroup>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="mensaje"
                      id="mensaje"
                      placeholder="Añadir nuevo comentario"
                    />
                  </FormGroup>
                </Col>
                <Col lg={2}>
                  <FormGroup>
                    <Input
                      onChange={this.onChange}
                      type="text"
                      name="autor"
                      id="autor"
                      placeholder="Autor"
                    />
                  </FormGroup>
                </Col>
                <Col lg={2}>
                  <Button className="bg-success border-success">Subir</Button>
                </Col>
              </Row>
            </Form>
          </CardBody>
        </Card>
      </div>
    );
  }
}

export default ComentariosDetalle;
