import React, { Component } from "react";
import BarcodeReader from "react-barcode-reader";

class TestQr extends Component {
  constructor() {
    super();
    this.state = {
      hasUserMedia: false,
    };
    this.scanBarcode = this.scanBarcode.bind(this);
  }
  scanBarcode() {
    if (window.reader) {
      let canvas = document.createElement("canvas");
      canvas.width = this.props.width;
      canvas.height = this.props.height;
      let ctx = canvas.getContext("2d");
      ctx.drawImage(this.video, 0, 0, this.props.width, this.props.height);

      window.reader
        .decodeBuffer(
          ctx.getImageData(0, 0, canvas.width, canvas.height).data,
          canvas.width,
          canvas.height,
          canvas.width * 4,
          window.dynamsoft.BarcodeReader.EnumImagePixelFormat.IPF_ARGB_8888
        )
        .then((results) => {
          this.showResults(results);
        });
    }
  }
  render() {
    return (
      <div id="videoview" width={this.props.width} height={this.props.height}>
        <button onClick={this.scanBarcode}>Scan Barcodes</button>
        <video
          autoPlay
          width={this.props.width}
          height={this.props.height}
          src={this.state.src}
          muted={this.props.audio}
          className={this.props.className}
          playsInline
          style={this.props.style}
          ref={(ref) => {
            this.video = ref;
          }}
        />
        <canvas
          id="overlay"
          width={this.props.width}
          height={this.props.height}
        ></canvas>
      </div>
    );
  }
}
export default TestQr;
