import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Card,
  CardBody,
  Row,
  Col,
  Table,
  Container,
} from "reactstrap";
import { Link } from "react-router-dom";
import moment from "moment";

const TableModal = (props) => {
  const { buttonLabel, className } = props;

  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);

  return (
    <div>
      <Button onClick={toggle}>↓</Button>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Tabla de tarjetas</ModalHeader>
        <ModalBody>
          <Card>
            <CardBody>
              <Table className="no-wrap v-middle" responsive>
                <thead>
                  <tr className="border-0">
                    <th className="border-0">Mes</th>
                    {props.fechas.map((item) => {
                      return <th className="border-0">{item}</th>;
                    })}
                  </tr>
                </thead>
                {props.tarjetas &&
                  props.tarjetas.map(
                    ({
                      numero,
                      color,
                      prioridad,
                      equipo,
                      fecha,
                      descripcion,
                      estado,
                      _id,
                    }) => {
                      const timeDiferrence = moment().diff(fecha, "days");
                      return (
                        <tbody key={_id}>
                          <tr>
                            <td>Acumuladas (abiertas)</td>

                            <td>Acumuladas (cerradas)</td>
                            <td>Porcentaje (cerradas)</td>
                            <td>
                              {prioridad === "Alta" && timeDiferrence >= 15 && (
                                <li className="border-0 p-0 text-danger list-inline-item">
                                  <i className="fa fa-circle">
                                    <div>&nbsp;</div>
                                  </i>
                                </li>
                              )}
                              {prioridad === "Media" && timeDiferrence >= 30 && (
                                <li className="border-0 p-0 text-danger list-inline-item">
                                  <i className="fa fa-circle">
                                    <div>&nbsp;</div>
                                  </i>
                                </li>
                              )}
                              {prioridad === "Baja" && timeDiferrence >= 60 && (
                                <li className="border-0 p-0 text-danger list-inline-item">
                                  <i className="fa fa-circle">
                                    <div>&nbsp;</div>
                                  </i>
                                </li>
                              )}
                              {prioridad}
                            </td>

                            <td>{estado}</td>
                          </tr>
                        </tbody>
                      );
                    }
                  )}
              </Table>
            </CardBody>
          </Card>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default TableModal;
