import React, { Component } from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Container,
  Row,
  Col,
  Alert,
} from "reactstrap";
import { getTarjetas } from "../../store/actions/tarjetaActions";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import { agregarTarjeta } from "../../store/actions/tarjetaActions";

class AñadirTarjeta extends Component {
  componentDidMount() {
    this.props.getTarjetas();
  }
  state = {
    numero: "",
    descripcion: "",
    color: "",
    detecto: "",
    prioridad: "",
    familia: "",
    maquina: "",
    equipo: "",
    riesgoInicial: "",
    tipodeRiesgo: "",
    sugerencia: "false",
    sustoExperimentado: false,
    sustoObservado: false,
    impactoAmbiente: false,
    msg: null,
  };

  componentDidUpdate(prevProps) {
    const { error } = this.props;
    if (error !== prevProps.error) {
      // Check login error
      console.log(error.msg.msg);
      if (error.id === "AGREGAR_TARJETA_ERROR") {
        this.setState({
          msg: error.msg.msg,
        });
      } else {
        this.setState({
          msg: null,
        });
      }
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onChangeColor = (e) => {
    const numeroTarjeta = this.props.tarjetas.tarjetas.filter(
      ({ color }) => color === e.target.value
    );
    this.setState({
      [e.target.name]: e.target.value,
      numero: numeroTarjeta.length + 1,
    });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const {
      numero,
      descripcion,
      color,
      detecto,
      prioridad,
      familia,
      maquina,
      equipo,
      riesgoInicial,
      tipodeRiesgo,
    } = this.state;

    // Crear Tarjeta
    const nuevaTarjeta = {
      numero,
      descripcion,
      color,
      detecto,
      prioridad,
      familia,
      maquina,
      equipo,
      riesgoInicial,
      tipodeRiesgo,
    };
    this.props.agregarTarjeta(nuevaTarjeta);
  };
  render() {
    if (this.props.tarjetas.agregarsuccess) {
      return <Redirect to="/tarjetas" />;
    }
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <h2 className="my-2">Añadir nueva tarjeta</h2>
              <Form className="mt-4" onSubmit={this.onSubmit}>
                <FormGroup>
                  <Label for="color">Color *</Label>
                  <Input
                    type="select"
                    name="color"
                    id="color"
                    onChange={this.onChangeColor}
                  >
                    <option>Seleccionar</option>
                    <option>Roja</option>
                    <option>Azul</option>
                    <option>Verde</option>
                    <option>Amarilla</option>
                  </Input>
                </FormGroup>

                {this.state.color === "Amarilla" && (
                  <Row className="my-4">
                    <Col>
                      <FormGroup check>
                        <Label check>
                          <Input
                            type="checkbox"
                            id="sustoExperimentado"
                            name="sustoExperimentado"
                            onChange={(e) => {
                              this.onChange({
                                target: {
                                  name: e.target.name,
                                  value: e.target.checked,
                                },
                              });
                            }}
                          />
                          Reporte de Incidente "Susto" experimentado
                        </Label>
                      </FormGroup>
                    </Col>
                    <Col>
                      <FormGroup check>
                        <Label check>
                          <Input
                            type="checkbox"
                            id="sustoObservado"
                            name="sustoObservado"
                            onChange={(e) => {
                              this.onChange({
                                target: {
                                  name: e.target.name,
                                  value: e.target.checked,
                                },
                              });
                            }}
                          />
                          Reporte de Incidente "Susto" observado
                        </Label>
                      </FormGroup>
                    </Col>
                    <Col>
                      <FormGroup check>
                        <Label check>
                          <Input
                            type="checkbox"
                            id="impactoAmbiente"
                            name="impactoAmbiente"
                            onChange={(e) => {
                              this.onChange({
                                target: {
                                  name: e.target.name,
                                  value: e.target.checked,
                                },
                              });
                            }}
                          />
                          El incidente puede afectar al Medio Ambiente
                        </Label>
                      </FormGroup>
                    </Col>
                  </Row>
                )}

                <Row>
                  <Col>
                    <FormGroup>
                      <Label for="maquina">Equipamiento (donde) *</Label>
                      <Input
                        type="select"
                        name="maquina"
                        id="maquina"
                        onChange={this.onChange}
                      >
                        <option>Seleccionar</option>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                      </Input>
                    </FormGroup>

                    <FormGroup>
                      <Label for="prioridad">Prioridad *</Label>
                      <Input
                        type="select"
                        name="prioridad"
                        id="prioridad"
                        onChange={this.onChange}
                      >
                        <option>Seleccionar</option>
                        <option>Alta</option>
                        <option>Media</option>
                        <option>Baja</option>
                      </Input>
                    </FormGroup>
                    <FormGroup>
                      <Label for="detecto">Detecto *</Label>
                      <Input
                        type="text"
                        name="detecto"
                        id="detecto"
                        onChange={this.onChange}
                      />
                    </FormGroup>

                    <FormGroup>
                      {this.state.color !== "Amarilla" ? (
                        <Label for="descripcion">Descripcion *</Label>
                      ) : (
                        <Label for="descripcion">
                          Descripcion del incidente "SUSTO" experimentado u
                          observado *
                        </Label>
                      )}
                      <Input
                        type="textarea"
                        name="descripcion"
                        id="descripcion"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                  </Col>
                  <Col>
                    <FormGroup>
                      <Label for="equipo">Equipo Autonomo*</Label>
                      <Input
                        type="select"
                        name="equipo"
                        id="equipo"
                        onChange={this.onChange}
                      >
                        <option>Seleccionar</option>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                        <option>6</option>
                        <option>7</option>
                        <option>8</option>
                        <option>9</option>
                        <option>10</option>
                      </Input>
                    </FormGroup>

                    {this.state.color !== "Amarilla" && (
                      <FormGroup>
                        <Label for="familia">Familia de anomalias *</Label>
                        <Input
                          type="select"
                          name="familia"
                          id="familia"
                          onChange={this.onChange}
                        >
                          <option>Seleccionar</option>
                          <option>1 - Pequeñas Deficiencia</option>
                          <option>2 - Condiciones basicas</option>
                          <option>3 - Puntos inaccesibles</option>
                          <option>4 - Focos de contaminacion</option>
                          <option>5 - Defecto de Calidad</option>
                          <option>6 - Elementos Innecesarios</option>
                          <option>7 - Lugares Inseguros</option>
                        </Input>
                      </FormGroup>
                    )}
                    {this.state.color === "Amarilla" && (
                      <FormGroup>
                        <Label for="detecto">
                          ¿Que sugiere para que no se repita?*
                        </Label>
                        <Input
                          type="text"
                          name="sugerencia"
                          id="sugerencia"
                          onChange={this.onChange}
                        />
                      </FormGroup>
                    )}

                    <FormGroup>
                      {this.state.color !== "Amarilla" ? (
                        <Label for="detecto">Tipo *</Label>
                      ) : (
                        <Label for="detecto">Tipo de riesgo *</Label>
                      )}
                      <Input
                        type="text"
                        name="tipodeRiesgo"
                        id="tipodeRiesgo"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                    <FormGroup>
                      <Label for="detecto">Riesgo Inicial *</Label>
                      <Input
                        type="text"
                        name="riesgoInicial"
                        id="riesgoInicial"
                        onChange={this.onChange}
                      />
                    </FormGroup>
                  </Col>
                </Row>
                {this.state.msg ? (
                  <Alert color="danger" className="mt-3">
                    {this.state.msg}
                  </Alert>
                ) : null}
                <Row className="mt-3">
                  <Col>
                    <Button>Subir</Button>
                  </Col>
                </Row>
              </Form>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tarjetas: state.tarjetas,
    error: state.error,
  };
};
export default connect(mapStateToProps, { agregarTarjeta, getTarjetas })(
  AñadirTarjeta
);
