import React, { Component } from "react";
import "./App.css";
import AñadirTarjeta from "./components/añadirtarjeta/AñadirTarjeta";
import DashBoard from "./components/dasboard/Dashboard";
import { BrowserRouter, Switch, Route, Redirect } from "react-router-dom";
import NavBar from "./components/navbar/NavBar";
import Footer from "./components/footer/footer";
import MisTarjetas from "./components/tarjetas/MisTarjetas";
import LoginPage from "./auth-components/login/loginpage";
import RegisterPage from "./auth-components/register/registerpage";
import LayoutDetalle from "./components/detalletarjeta/layout";
import { loadUser } from "./store/actions/authActions";
import { connect } from "react-redux";
import PrivateRoute from "./PrivateRoute";
import { Nav } from "reactstrap";
class App extends Component {
  componentDidMount() {
    this.props.store.dispatch(loadUser());
  }
  render() {
    return (
      <BrowserRouter>
        <NavBar></NavBar>
        <Switch>
          <Route exact path="/login">
            <LoginPage />
          </Route>
          <Route exact path="/register">
            <RegisterPage />
          </Route>
          <PrivateRoute
            exact
            path="/"
            component={DashBoard}
            isLoading={this.props.isLoading}
            authed={this.props.isAuthenticated}
            user={this.props.user}
          />
          <PrivateRoute
            path="/tarjetas"
            component={MisTarjetas}
            isLoading={this.props.isLoading}
            authed={this.props.isAuthenticated}
            user={this.props.user}
          />
          <PrivateRoute
            path="/agregartarjeta"
            component={AñadirTarjeta}
            isLoading={this.props.isLoading}
            authed={this.props.isAuthenticated}
            user={this.props.user}
          />
          <PrivateRoute
            path="/tarjeta/:id"
            component={LayoutDetalle}
            isLoading={this.props.isLoading}
            authed={this.props.isAuthenticated}
            user={this.props.user}
          />
        </Switch>
      </BrowserRouter>
    );
  }
}

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
  user: state.auth.user,
  isLoading: state.auth.isLoading,
});
export default connect(mapStateToProps)(App);
