import React, { Component } from "react";
import { Card, CardTitle, CardBody, Container, Row, Col } from "reactstrap";
import CanvasJSReact from "./canvasjs.react";

var CanvasJSChart = CanvasJSReact.CanvasJSChart;
class DashBoard extends Component {
  render() {
    const options = {
      data: [
        {
          type: "bar",
          dataPoints: [
            { label: "Total Bugs", y: 4, color: "#3483B3" },
            { label: "Open Bugs", y: 7, color: "#88DD45" },
            { label: "Closed Bugs", y: 2, color: "#DD4545" },
          ],
        },
      ],
    };
    const options2 = {
      data: [
        {
          type: "column",
          dataPoints: [
            { label: "Bug / Errors", y: 3, color: "#3483B3" },
            { label: "Task", y: 5, color: "#88DD45" },
          ],
        },
      ],
    };
    return (
      <div>
        <div className="page-wrapper d-block">
          <div className="page-content container-fluid">
            <Container>
              <div>
                <h2 className="mb-3">Dashboard</h2>
              </div>

              <Row>
                <Col>
                  <Card>
                    <CardBody>
                      <div className="d-flex align-items-center">
                        <div className="">
                          <CardTitle>Bugs by status</CardTitle>
                        </div>
                      </div>
                      <CanvasJSChart options={options} />
                    </CardBody>
                  </Card>
                </Col>
                <Col>
                  <Card>
                    <CardBody>
                      <div className="d-flex align-items-center">
                        <div className="">
                          <CardTitle>Bugs by type</CardTitle>
                        </div>
                      </div>
                      <CanvasJSChart options={options2} />
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </Container>
          </div>
        </div>
      </div>
    );
  }
}

export default DashBoard;
