import React, { useState } from "react";
import { Button, Modal, ModalHeader, ModalBody, Media } from "reactstrap";

const ImagenModal = (props) => {
  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  return (
    <div>
      <Button color="danger" onClick={toggle}>
        Ver imagen
      </Button>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Imagen</ModalHeader>
        <ModalBody>
          <Media
            object
            data-src={`${process.env.PUBLIC_URL}/uploads/${color + numero}.png`}
          ></Media>
          <Media left>
            <Media
              style={imgStyle}
              object
              src={`${process.env.PUBLIC_URL}/uploads/${color + numero}.png`}
              alt="Generic placeholder image"
            />
          </Media>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default ImagenModal;
